import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { authenticateToken, requireAdmin, generateToken, hashPassword, comparePassword, type AuthRequest } from "./auth";
import { loginSchema, registerSchema } from "@shared/schema";
import Stripe from "stripe";

if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error('Missing required Stripe secret: STRIPE_SECRET_KEY');
}
const stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2023-10-16",
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth routes
  app.post('/api/auth/register', async (req, res) => {
    try {
      const validatedData = registerSchema.parse(req.body);

      // Check if user exists
      const existingUser = await storage.getUserByEmail(validatedData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      // Hash password and create user
      const hashedPassword = await hashPassword(validatedData.password);
      const user = await storage.createUser({
        email: validatedData.email,
        password: hashedPassword,
        firstName: validatedData.firstName,
        lastName: validatedData.lastName,
        customerType: validatedData.customerType,
      });

      // Generate token
      const token = generateToken(user.id);

      res.json({
        token,
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
          customerType: user.customerType
        }
      });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(400).json({ message: "Registration failed" });
    }
  });

  app.post('/api/auth/login', async (req, res) => {
    try {
      const validatedData = loginSchema.parse(req.body);

      // Find user
      const user = await storage.getUserByEmail(validatedData.email);
      if (!user || !user.isActive) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Check password
      const isValidPassword = await comparePassword(validatedData.password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Generate token
      const token = generateToken(user.id);

      res.json({
        token,
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
          customerType: user.customerType
        }
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(400).json({ message: "Login failed" });
    }
  });

  app.get('/api/auth/user', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const user = await storage.getUser(req.user!.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        role: user.role,
        customerType: user.customerType
      });
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Categories routes
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Get categories error:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Products routes
  app.get('/api/products', async (req, res) => {
    try {
      const filters = {
        categoryId: req.query.categoryId as string,
        searchQuery: req.query.searchQuery as string,
        minPrice: req.query.minPrice ? Number(req.query.minPrice) : undefined,
        maxPrice: req.query.maxPrice ? Number(req.query.maxPrice) : undefined,
        location: req.query.location as string,
        availability: req.query.availability as string,
        ownerId: req.query.ownerId as string,
      };

      const products = await storage.getProducts(filters);
      res.json(products);
    } catch (error) {
      console.error("Get products error:", error);
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.get('/api/products/:id', async (req, res) => {
    try {
      const product = await storage.getProduct(req.params.id);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      console.error("Get product error:", error);
      res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  app.post('/api/products', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const productData = { ...req.body, ownerId: req.user!.id };
      const product = await storage.createProduct(productData);
      res.json(product);
    } catch (error) {
      console.error("Create product error:", error);
      res.status(500).json({ message: "Failed to create product" });
    }
  });

  app.get('/api/products/:id/pricing', async (req, res) => {
    try {
      const pricing = await storage.getProductPricing(req.params.id);
      res.json(pricing);
    } catch (error) {
      console.error("Get product pricing error:", error);
      res.status(500).json({ message: "Failed to fetch product pricing" });
    }
  });

  // Bookings routes
  app.get('/api/bookings', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const filters = {
        customerId: req.user!.id,
        productId: req.query.productId as string,
        status: req.query.status as string,
        startDate: req.query.startDate ? new Date(req.query.startDate as string) : undefined,
        endDate: req.query.endDate ? new Date(req.query.endDate as string) : undefined,
      };

      const bookings = await storage.getBookings(filters);
      res.json(bookings);
    } catch (error) {
      console.error("Get bookings error:", error);
      res.status(500).json({ message: "Failed to fetch bookings" });
    }
  });

  app.post('/api/bookings', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const bookingData = { ...req.body, customerId: req.user!.id };
      const booking = await storage.createBooking(bookingData);
      res.json(booking);
    } catch (error) {
      console.error("Create booking error:", error);
      res.status(500).json({ message: "Failed to create booking" });
    }
  });

  app.patch('/api/bookings/:id', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const booking = await storage.updateBooking(req.params.id, req.body);
      res.json(booking);
    } catch (error) {
      console.error("Update booking error:", error);
      res.status(500).json({ message: "Failed to update booking" });
    }
  });

  // Availability check
  app.post('/api/products/:id/check-availability', async (req, res) => {
    try {
      const { startDate, endDate } = req.body;
      const isAvailable = await storage.checkAvailability(
        req.params.id,
        new Date(startDate),
        new Date(endDate)
      );
      res.json({ available: isAvailable });
    } catch (error) {
      console.error("Check availability error:", error);
      res.status(500).json({ message: "Failed to check availability" });
    }
  });

  // Admin routes
  app.get('/api/admin/stats', authenticateToken, requireAdmin, async (req, res) => {
    try {
      const stats = await storage.getAdminStats();
      res.json(stats);
    } catch (error) {
      console.error("Get admin stats error:", error);
      res.status(500).json({ message: "Failed to fetch admin stats" });
    }
  });

  app.get('/api/admin/late-bookings', authenticateToken, requireAdmin, async (req, res) => {
    try {
      const lateBookings = await storage.getLateBookings();
      res.json(lateBookings);
    } catch (error) {
      console.error("Get late bookings error:", error);
      res.status(500).json({ message: "Failed to fetch late bookings" });
    }
  });

  // Configuration routes
  app.get('/api/config/durations', async (req, res) => {
    try {
      const durations = await storage.getDurationOptions();
      res.json(durations);
    } catch (error) {
      console.error("Get duration options error:", error);
      res.status(500).json({ message: "Failed to fetch duration options" });
    }
  });

  // Notifications
  app.get('/api/notifications', authenticateToken, async (req: AuthRequest, res) => {
    try {
      const notifications = await storage.getNotifications(req.user!.id);
      res.json(notifications);
    } catch (error) {
      console.error("Get notifications error:", error);
      res.status(500).json({ message: "Failed to fetch notifications" });
    }
  });

  // Cloudinary signature
  app.post('/api/cloudinary/signature', authenticateToken, async (req, res) => {
    try {
      const { timestamp, public_id, folder } = req.body;

      const signature = require('crypto')
        .createHash('sha256')
        .update(`folder=${folder}&public_id=${public_id}&timestamp=${timestamp}${process.env.CLOUDINARY_API_SECRET}`)
        .digest('hex');

      res.json({
        signature,
        timestamp,
        apiKey: process.env.CLOUDINARY_API_KEY,
        cloudName: process.env.CLOUDINARY_CLOUD_NAME,
      });
    } catch (error) {
      console.error("Cloudinary signature error:", error);
      res.status(500).json({ message: "Failed to generate signature" });
    }
  });

  // Stripe payment routes
  app.post("/api/create-payment-intent", authenticateToken, async (req, res) => {
    try {
      const { amount } = req.body;
      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: "usd",
      });
      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      res
        .status(500)
        .json({ message: "Error creating payment intent: " + error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  products,
  categories,
  bookings,
  productPricing,
  notifications,
  lateFees,
  durationOptions,
  statusConfig,
  businessConfig,
  type User,
  type InsertUser,
  type Product,
  type Category,
  type Booking,
  type ProductPricing,
  type Notification,
  type DurationOption,
  type StatusConfig,
  type BusinessConfig,
  type InsertProduct,
  type InsertBooking,
  type InsertProductPricing,
  type InsertNotification,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, gte, lte, desc, asc, sql, ilike, or } from "drizzle-orm";

export interface IStorage {
  // User operations (JWT Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User>;

  // Category operations
  getCategories(): Promise<Category[]>;

  // Product operations
  getProducts(filters?: {
    categoryId?: string;
    searchQuery?: string;
    minPrice?: number;
    maxPrice?: number;
    location?: string;
    availability?: string;
    ownerId?: string;
  }): Promise<Product[]>;
  getProduct(id: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: string, product: Partial<InsertProduct>): Promise<Product>;
  deleteProduct(id: string): Promise<void>;

  // Product pricing operations
  getProductPricing(productId: string): Promise<ProductPricing[]>;
  createProductPricing(pricing: InsertProductPricing): Promise<ProductPricing>;
  updateProductPricing(id: string, pricing: Partial<InsertProductPricing>): Promise<ProductPricing>;

  // Booking operations
  getBookings(filters?: {
    customerId?: string;
    productId?: string;
    status?: string;
    startDate?: Date;
    endDate?: Date;
  }): Promise<Booking[]>;
  getBooking(id: string): Promise<Booking | undefined>;
  createBooking(booking: InsertBooking): Promise<Booking>;
  updateBooking(id: string, booking: Partial<InsertBooking>): Promise<Booking>;
  checkAvailability(productId: string, startDate: Date, endDate: Date, excludeBookingId?: string): Promise<boolean>;

  // Late fees operations
  calculateLateFees(bookingId: string): Promise<number>;
  getLateBookings(): Promise<Booking[]>;

  // Notification operations
  getNotifications(userId: string): Promise<Notification[]>;
  createNotification(notification: InsertNotification): Promise<Notification>;
  markNotificationRead(id: string): Promise<void>;

  // Configuration operations
  getDurationOptions(): Promise<DurationOption[]>;
  getStatusConfig(): Promise<StatusConfig[]>;
  getBusinessConfig(key?: string): Promise<BusinessConfig[]>;
  updateBusinessConfig(key: string, value: string): Promise<BusinessConfig>;

  // Analytics operations
  getAdminStats(): Promise<{
    activeRentals: number;
    lateReturns: number;
    monthlyRevenue: number;
    pendingPickups: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(userData: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db
      .select()
      .from(categories)
      .where(eq(categories.isActive, true))
      .orderBy(asc(categories.name));
  }

  // Product operations
  async getProducts(filters?: {
    categoryId?: string;
    searchQuery?: string;
    minPrice?: number;
    maxPrice?: number;
    location?: string;
    availability?: string;
    ownerId?: string;
  }): Promise<Product[]> {
    const conditions = [eq(products.isActive, true)];

    if (filters) {
      if (filters.categoryId) {
        conditions.push(eq(products.categoryId, filters.categoryId));
      }

      if (filters.searchQuery) {
        conditions.push(
          or(
            ilike(products.name, `%${filters.searchQuery}%`),
            ilike(products.description, `%${filters.searchQuery}%`)
          )!
        );
      }

      if (filters.location) {
        conditions.push(ilike(products.location, `%${filters.location}%`));
      }

      if (filters.ownerId) {
        conditions.push(eq(products.ownerId, filters.ownerId));
      }
    }

    return await db.select()
      .from(products)
      .where(and(...conditions))
      .orderBy(desc(products.createdAt));
  }

  async getProduct(id: string): Promise<Product | undefined> {
    const [product] = await db.select().from(products).where(eq(products.id, id));
    return product;
  }

  async createProduct(product: InsertProduct): Promise<Product> {
    const [newProduct] = await db.insert(products).values(product).returning();
    return newProduct;
  }

  async updateProduct(id: string, product: Partial<InsertProduct>): Promise<Product> {
    const [updatedProduct] = await db
      .update(products)
      .set({ ...product, updatedAt: new Date() })
      .where(eq(products.id, id))
      .returning();
    return updatedProduct;
  }

  async deleteProduct(id: string): Promise<void> {
    await db.update(products).set({ isActive: false }).where(eq(products.id, id));
  }

  // Product pricing operations
  async getProductPricing(productId: string): Promise<ProductPricing[]> {
    return await db
      .select()
      .from(productPricing)
      .where(and(eq(productPricing.productId, productId), eq(productPricing.isActive, true)));
  }

  async createProductPricing(pricing: InsertProductPricing): Promise<ProductPricing> {
    const [newPricing] = await db.insert(productPricing).values(pricing).returning();
    return newPricing;
  }

  async updateProductPricing(id: string, pricing: Partial<InsertProductPricing>): Promise<ProductPricing> {
    const [updatedPricing] = await db
      .update(productPricing)
      .set(pricing)
      .where(eq(productPricing.id, id))
      .returning();
    return updatedPricing;
  }

  // Booking operations
  async getBookings(filters?: {
    customerId?: string;
    productId?: string;
    status?: string;
    startDate?: Date;
    endDate?: Date;
  }): Promise<Booking[]> {
    const conditions = [];

    if (filters) {
      if (filters.customerId) {
        conditions.push(eq(bookings.customerId, filters.customerId));
      }

      if (filters.productId) {
        conditions.push(eq(bookings.productId, filters.productId));
      }

      if (filters.status) {
        conditions.push(eq(bookings.status, filters.status as any));
      }

      if (filters.startDate) {
        conditions.push(gte(bookings.startDate, filters.startDate));
      }

      if (filters.endDate) {
        conditions.push(lte(bookings.endDate, filters.endDate));
      }
    }

    if (conditions.length > 0) {
      return await db.select()
        .from(bookings)
        .where(and(...conditions))
        .orderBy(desc(bookings.createdAt));
    } else {
      return await db.select()
        .from(bookings)
        .orderBy(desc(bookings.createdAt));
    }
  }

  async getBooking(id: string): Promise<Booking | undefined> {
    const [booking] = await db.select().from(bookings).where(eq(bookings.id, id));
    return booking;
  }

  async createBooking(booking: InsertBooking): Promise<Booking> {
    const [newBooking] = await db.insert(bookings).values(booking).returning();
    return newBooking;
  }

  async updateBooking(id: string, booking: Partial<InsertBooking>): Promise<Booking> {
    const [updatedBooking] = await db
      .update(bookings)
      .set({ ...booking, updatedAt: new Date() })
      .where(eq(bookings.id, id))
      .returning();
    return updatedBooking;
  }

  async checkAvailability(productId: string, startDate: Date, endDate: Date, excludeBookingId?: string): Promise<boolean> {
    const conditions = [
      eq(bookings.productId, productId),
      or(
        and(gte(bookings.startDate, startDate), lte(bookings.startDate, endDate)),
        and(gte(bookings.endDate, startDate), lte(bookings.endDate, endDate)),
        and(lte(bookings.startDate, startDate), gte(bookings.endDate, endDate))
      )
    ];

    if (excludeBookingId) {
      conditions.push(sql`${bookings.id} != ${excludeBookingId}`);
    }

    const conflictingBookings = await db
      .select()
      .from(bookings)
      .where(and(...conditions));

    return conflictingBookings.length === 0;
  }

  // Late fees operations
  async calculateLateFees(bookingId: string): Promise<number> {
    const booking = await this.getBooking(bookingId);
    if (!booking || booking.status !== 'late') return 0;

    const today = new Date();
    const endDate = new Date(booking.endDate);
    const daysLate = Math.floor((today.getTime() - endDate.getTime()) / (1000 * 60 * 60 * 24));

    if (daysLate <= 0) return 0;

    // Get late fee rate from business config (default 5%)
    const [config] = await this.getBusinessConfig('late_fee_rate');
    const lateFeeRate = config ? parseFloat(config.value) : 5;

    const dailyFee = (parseFloat(booking.basePrice) * lateFeeRate) / 100;
    return dailyFee * daysLate;
  }

  async getLateBookings(): Promise<Booking[]> {
    const today = new Date();
    return await db
      .select()
      .from(bookings)
      .where(
        and(
          eq(bookings.status, 'active' as any),
          lte(bookings.endDate, today)
        )
      );
  }

  // Notification operations
  async getNotifications(userId: string): Promise<Notification[]> {
    return await db
      .select()
      .from(notifications)
      .where(eq(notifications.userId, userId))
      .orderBy(desc(notifications.createdAt));
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const [newNotification] = await db.insert(notifications).values(notification).returning();
    return newNotification;
  }

  async markNotificationRead(id: string): Promise<void> {
    await db.update(notifications).set({ isRead: true }).where(eq(notifications.id, id));
  }

  // Configuration operations
  async getDurationOptions(): Promise<DurationOption[]> {
    return await db
      .select()
      .from(durationOptions)
      .where(eq(durationOptions.isActive, true))
      .orderBy(asc(durationOptions.sortOrder));
  }

  async getStatusConfig(): Promise<StatusConfig[]> {
    return await db
      .select()
      .from(statusConfig)
      .where(eq(statusConfig.isActive, true));
  }

  async getBusinessConfig(key?: string): Promise<BusinessConfig[]> {
    if (key) {
      return await db.select().from(businessConfig).where(eq(businessConfig.key, key));
    }
    return await db.select().from(businessConfig);
  }

  async updateBusinessConfig(key: string, value: string): Promise<BusinessConfig> {
    const [config] = await db
      .insert(businessConfig)
      .values({ key, value, updatedAt: new Date() })
      .onConflictDoUpdate({
        target: businessConfig.key,
        set: { value, updatedAt: new Date() },
      })
      .returning();
    return config;
  }

  // Analytics operations
  async getAdminStats(): Promise<{
    activeRentals: number;
    lateReturns: number;
    monthlyRevenue: number;
    pendingPickups: number;
  }> {
    const today = new Date();
    const monthStart = new Date(today.getFullYear(), today.getMonth(), 1);

    // Active rentals
    const [{ count: activeRentals }] = await db
      .select({ count: sql<number>`count(*)` })
      .from(bookings)
      .where(eq(bookings.status, 'active' as any));

    // Late returns
    const [{ count: lateReturns }] = await db
      .select({ count: sql<number>`count(*)` })
      .from(bookings)
      .where(
        and(
          eq(bookings.status, 'active' as any),
          lte(bookings.endDate, today)
        )
      );

    // Monthly revenue
    const [{ revenue }] = await db
      .select({ revenue: sql<number>`COALESCE(SUM(${bookings.totalAmount}), 0)` })
      .from(bookings)
      .where(
        and(
          gte(bookings.createdAt, monthStart),
          lte(bookings.createdAt, today)
        )
      );

    // Pending pickups (next 24 hours)
    const tomorrow = new Date(today.getTime() + 24 * 60 * 60 * 1000);
    const [{ count: pendingPickups }] = await db
      .select({ count: sql<number>`count(*)` })
      .from(bookings)
      .where(
        and(
          eq(bookings.status, 'confirmed' as any),
          gte(bookings.startDate, today),
          lte(bookings.startDate, tomorrow)
        )
      );

    return {
      activeRentals: Number(activeRentals),
      lateReturns: Number(lateReturns),
      monthlyRevenue: Number(revenue),
      pendingPickups: Number(pendingPickups),
    };
  }
}

export const storage = new DatabaseStorage();

